let cardcontainer = document.querySelector('.card-container')
axios.get('/api/allSchemes').then(res => res.data).then((data) => {
  console.log(data);
  for (let i = 0; i < data.schemes.length; i++) {
    let scheme = data.schemes[i];
    console.log(scheme._id)
    let card = document.createElement('div');
    card.innerHTML = `<div class="card m-2" style="width: 18rem;">
    <img src="data:image/png;base64," class="card-img-top" alt="...">
    <div class="card-body">
    <h5 class="card-title">${scheme.title}</h5>
    <p class="card-text">${scheme.description}</p>
    <p class="card-text">Eligibility - ${scheme.eligibility}</p>
    <p class="card-text">${scheme.date}</p>
    <p class="card-text">Govt Agency - ${scheme.agency}</p>
      <a href="#" class="btn btn-primary" onclick="enrollApplication('${scheme._id}')">enroll</a>
    </div>
  </div>
  `
    cardcontainer.appendChild(card)
  }
})

function enrollApplication(param) {
  console.log(param)
  let text = "are you sure you are eligible for this scheme";
  if (confirm(text) == true) {
    axios.post('/api/scheme/apply', { schemeId: param }).then(res => res.data).then((data) => {
      console.log(data)
      if (data.status == true) {
        alert("done")
      }
    }).catch(err => {
      alert("done")
    })
  }
}